package cn.cunchang.最小覆盖子串_76_困难;

class Solution2 {
    /**
     * 思考：
     * -------------------------------------------------------------------
     * 思路：
     *
     * -------------------------------------------------------------------
     * 时间复杂度：
     * 空间复杂度：
     */
    public String minWindow(String s, String t) {
        if (s.length() < t.length()) {
            return ""; //"a","aa"
        }
        if (s.equals(t)) {
            return s;
        }
        int l = 0, r = 0;
        String target = "";
        while (l <= s.length() && r <= s.length()) {
            if (r - l >= t.length()) {
                String sub = s.substring(l, r);
                if (isSub(sub, t)) {
                    if ("".equals(target)) {// 第一次找到
                        target = sub;
                    }
                    if (target.length() > sub.length()) { //第n次找到，要最优的
                        target = sub;
                    }
                    l++;
//                    r = l;
                }
            }
            r++;
        }
        return target;
    }

    private boolean isSub(String sub, String t) {
        if (sub.length() < t.length()) {
            return false;
        }
        int[] set = new int[128];
        for (char c : t.toCharArray()) {
            set[c]++;//记录t字符
        }
        for (char c : sub.toCharArray()) {
            set[c]--;//删除t字符
        }
        for (char c : t.toCharArray()) {
            if (set[c] > 0) {// 说明有t字符没有被删除，即sub不完全包含t
                return false;
            }
        }

        return true;
    }

    public static void main(String[] args) {
//        String s = "ADOBECODEBANC", t = "ABC";
//        System.out.println(new Solution().minWindow(s, t));
        String s = "",
                t = "";
        System.out.println(new Solution2().minWindow(s, t));
    }
}